import React from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.css";

class App extends React.Component {
  state = { question: null, answer: null };
  GetItemsFromAPI = () => {
    this.setState({ question: "question1", answer: "answer1" });
  };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img
            src={
              "https://upload.wikimedia.org/wikipedia/commons/6/6c/Star_Wars_Logo.svg"
            }
            height="420"
            width="420"
            alt="logo"
          />

          <button className="specialcls" onClick={this.GetItemsFromAPI}>
            Do. Or do not. There is no try
          </button>
          <div>{this.state.apiresult}</div>
        </header>
      </div>
    );
  }
}

export default App;
