import React from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.css";

class App extends React.Component {
  state = {
    question1: null,
    answer1: null,
    question2: null,
    answer2: null,
    question3: null,
    answer3: null,
    question4: null,
    answer4: null
  };
  GetItemsFromAPI = () => {
    axios
      .get("http://localhost:44362/api/starwars/getlongestopeningcrawlmovie")
      .then(response => {
        this.setState({
          question1:
            "Which of all Star Wars movies has the longest opening crawl?",
          answer1: "answer1"
        });
      })
      .catch(function(error) {
        console.log(error);
      });

    this.setState({
      question2:
        "What character (person) appeared in most of the Star Wars films?",
      answer2: "answer2",
      question3:
        "What species appeared in the most  number of Star Wars films?",
      answer3: "answer3",
      question4:
        "What planet in Star Wars universe provided largest number of vehicle pilots?",
      answer4: "answer4"
    });
  };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img
            src={
              "https://upload.wikimedia.org/wikipedia/commons/6/6c/Star_Wars_Logo.svg"
            }
            height="420"
            width="420"
            alt="logo"
          />

          <button className="specialcls" onClick={this.GetItemsFromAPI}>
            Do. Or do not. There is no try
          </button>
          <div>{this.state.question1}</div>
          <div>{this.state.answer1}</div>
          <div>{this.state.question2}</div>
          <div>{this.state.answer2}</div>
          <div>{this.state.question3}</div>
          <div>{this.state.answer3}</div>
          <div>{this.state.question4}</div>
          <div>{this.state.answer4}</div>
        </header>
      </div>
    );
  }
}

export default App;
